import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import api from '../api/api';
import { Product } from '../types/Product';

const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get<Product[]>('/products');
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen text-xl font-semibold text-gray-600">
        Loading products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen text-xl text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="font-sans text-gray-900">
      {/* --- Hero Section --- */}
      <section
        className="relative h-[90vh] flex flex-col justify-center items-center text-center px-6 md:px-12 bg-gradient-to-r from-indigo-900 via-indigo-800 to-indigo-700 overflow-hidden"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1500&q=80')",
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-60"></div>
        <h1 className="relative z-10 text-5xl md:text-7xl font-extrabold tracking-tight text-white drop-shadow-lg animate-fadeInDown">
          Revolutionize Your Lifestyle
        </h1>
        <p className="relative z-10 mt-6 max-w-xl text-lg md:text-xl text-indigo-200 animate-fadeInUp">
          Premium gadgets, smart products, and exclusive deals.
        </p>
        <button
          className="relative z-10 mt-10 bg-indigo-500 hover:bg-indigo-600 transition rounded-full px-8 py-4 font-semibold text-white shadow-lg shadow-indigo-700/50 animate-pulse hover:animate-none"
          onClick={() => window.scrollTo({ top: 700, behavior: 'smooth' })}
          aria-label="Shop Now"
        >
          Shop Now
        </button>
      </section>

      {/* --- Category Section --- */}
      <section className="py-16 bg-gray-50">
        <h2 className="text-3xl font-extrabold text-center mb-12 tracking-wide text-gray-900">
          Shop by Category
        </h2>
        <div className="grid grid-cols-2 sm:grid-cols-4 gap-8 px-4 md:px-20 max-w-7xl mx-auto">
          {['Phones', 'Wearables', 'Audio', 'Smart Home'].map((cat) => (
            <div
              key={cat}
              className="bg-white rounded-xl p-6 flex flex-col items-center shadow-lg hover:shadow-xl transition cursor-pointer group"
              role="button"
              tabIndex={0}
              onClick={() => alert(`Explore ${cat}`)}
              onKeyDown={(e) => e.key === 'Enter' && alert(`Explore ${cat}`)}
            >
              <img
                src={`https://via.placeholder.com/120?text=${cat}`}
                alt={cat}
                className="mb-4 w-24 h-24 object-contain group-hover:scale-110 transition-transform duration-300"
              />
              <p className="font-semibold text-lg text-gray-800">{cat}</p>
            </div>
          ))}
        </div>
      </section>

      {/* --- Featured Products Section --- */}
      <section className="py-20 px-6 md:px-16 bg-white max-w-4xl mx-auto">
        <h2 className="text-4xl font-extrabold text-center mb-14 tracking-wide text-gray-900">
          Featured Products
        </h2>
        <div className="grid grid-cols-1 gap-10">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      </section>

      {/* --- Trending Products --- */}
      <section className="py-16 bg-indigo-50">
        <h2 className="text-3xl font-extrabold text-center mb-12 tracking-wide text-indigo-900">
          Trending Now
        </h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-8 px-8 max-w-6xl mx-auto">
          {['Drone', 'VR Headset', 'Smart Light', 'Portable Speaker'].map((item) => (
            <div
              key={item}
              className="bg-white shadow-md p-6 text-center rounded-xl hover:shadow-xl transition cursor-pointer group"
              role="button"
              tabIndex={0}
              onClick={() => alert(`See more about ${item}`)}
              onKeyDown={(e) => e.key === 'Enter' && alert(`See more about ${item}`)}
            >
              <img
                src={`https://via.placeholder.com/160?text=${item}`}
                alt={item}
                className="mb-4 mx-auto w-32 h-32 object-contain group-hover:scale-105 transition-transform duration-300"
              />
              <p className="font-semibold text-indigo-900">{item}</p>
            </div>
          ))}
        </div>
      </section>

      {/* --- Features Section --- */}
      <section className="bg-indigo-700 text-white py-20 px-6">
        <h2 className="text-4xl font-extrabold text-center mb-14 tracking-wide">
          Why Choose Us
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-12 max-w-5xl mx-auto text-center">
          <div>
            <h4 className="text-2xl font-semibold mb-4">Free Shipping</h4>
            <p className="text-indigo-200 text-lg">On all orders above ₹499.</p>
          </div>
          <div>
            <h4 className="text-2xl font-semibold mb-4">Easy Returns</h4>
            <p className="text-indigo-200 text-lg">7-day hassle-free return policy.</p>
          </div>
          <div>
            <h4 className="text-2xl font-semibold mb-4">Secure Checkout</h4>
            <p className="text-indigo-200 text-lg">100% secure payments & data protection.</p>
          </div>
        </div>
      </section>

      {/* --- Testimonials Section --- */}
      <section className="py-20 bg-gray-100 px-6 max-w-7xl mx-auto">
        <h2 className="text-3xl font-extrabold text-center mb-14 tracking-wide text-gray-900">
          What Our Customers Say
        </h2>
        <div className="grid md:grid-cols-3 gap-10">
          {[
            'Excellent product range!',
            'Lightning-fast delivery!',
            'Great customer support!',
          ].map((quote, idx) => (
            <div
              key={idx}
              className="bg-white p-8 rounded-xl shadow-lg text-center hover:shadow-2xl transition cursor-default"
              aria-label={`Customer testimonial ${idx + 1}`}
            >
              <p className="italic text-gray-700 mb-6 text-lg">"{quote}"</p>
              <p className="font-semibold text-gray-600">— Customer {idx + 1}</p>
            </div>
          ))}
        </div>
      </section>

      {/* --- Newsletter Signup --- */}
      <section className="bg-indigo-100 py-16 px-6 text-center">
        <h2 className="text-3xl font-extrabold mb-6 tracking-wide text-indigo-900">
          Subscribe to Our Newsletter
        </h2>
        <p className="text-indigo-700 mb-8 max-w-xl mx-auto">
          Get updates on latest deals and upcoming product launches.
        </p>
        <form
          className="flex flex-col sm:flex-row justify-center gap-4 max-w-xl mx-auto"
          onSubmit={(e) => {
            e.preventDefault();
            alert('Subscribed successfully!');
          }}
        >
          <input
            type="email"
            placeholder="Enter your email"
            required
            className="px-4 py-3 rounded-lg border border-indigo-300 w-full sm:w-2/3 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            type="submit"
            className="bg-indigo-600 text-white px-8 py-3 rounded-lg hover:bg-indigo-700 transition font-semibold"
          >
            Subscribe
          </button>
        </form>
      </section>

      {/* --- Contact CTA --- */}
      <section className="bg-gray-900 text-white py-20 text-center px-6">
        <h2 className="text-4xl font-extrabold mb-6 tracking-wide">Need Help?</h2>
        <p className="mb-8 max-w-lg mx-auto text-lg">
          Contact our support team anytime. We’re here to help you.
        </p>
        <a
          href="mailto:support@example.com"
          className="inline-block bg-indigo-600 hover:bg-indigo-700 transition px-10 py-4 rounded-full font-semibold"
          aria-label="Contact support via email"
        >
          Contact Support
        </a>
      </section>
    </div>
  );
};

export default HomePage;
