import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard'; // Optional: You can replace ProductCard with inline card if you want
import api from '../api/api';
import { Product } from '../types/Product';

const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [newsletterEmail, setNewsletterEmail] = useState<string>('');
  const [newsletterMsg, setNewsletterMsg] = useState<string>('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get<Product[]>('/products');
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  const handleNewsletterSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newsletterEmail.match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/)) {
      setNewsletterMsg('Please enter a valid email address.');
      return;
    }
    setNewsletterMsg('Thank you for subscribing!');
    setNewsletterEmail('');
  };

  if (loading) {
    return (
      <div className="text-center mt-8 p-4 text-xl font-semibold text-gray-700">
        Loading products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center mt-8 p-4 text-xl text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="font-sans text-gray-900 max-w-full">
      {/* Hero Section */}
      <section
        className="relative bg-cover bg-center h-screen flex items-center justify-center text-white"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1510552776732-08b53d4e5d7e?auto=format&fit=crop&w=1500&q=80')",
        }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-60"></div>
        <div className="relative z-10 text-center max-w-4xl px-6">
          <h1 className="text-5xl md:text-6xl font-extrabold mb-6 leading-tight drop-shadow-lg">
            Revolutionize Your Lifestyle
          </h1>
          <p className="text-xl md:text-2xl mb-10 drop-shadow-md">
            Premium gadgets, smart products, and exclusive deals.
          </p>
          <button className="bg-indigo-600 hover:bg-indigo-700 transition text-white font-semibold px-8 py-4 rounded-full shadow-lg">
            Shop Now
          </button>
        </div>
      </section>

      {/* Category Section */}
      <section className="py-16 bg-gray-100 max-w-full px-4 md:px-10">
        <h2 className="text-3xl font-extrabold text-center mb-12 tracking-wide text-gray-900">
          Shop by Category
        </h2>
        <div className="grid grid-cols-2 sm:grid-cols-4 gap-6 max-w-7xl mx-auto">
          {['Phones', 'Wearables', 'Audio', 'Smart Home'].map((cat, idx) => (
            <div
              key={idx}
              tabIndex={0}
              className="bg-white rounded-xl p-6 text-center shadow hover:shadow-lg transition transform hover:scale-105 focus:outline-none focus:ring-4 focus:ring-indigo-500 cursor-pointer"
            >
              <img
                src={`https://images.unsplash.com/photo-1512499617640-c2f9992c136b?auto=format&fit=crop&w=100&q=60&sat=-100&blend=111827&blendMode=multiply&h=100&ixid=M3w5MjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D`}
                alt={cat}
                className="mx-auto mb-4 w-20 h-20 object-contain"
              />
              <p className="font-semibold text-lg text-gray-900">{cat}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Featured Products Section */}
      <section className="py-20 px-4 md:px-10 bg-white max-w-full">
        <h2 className="text-4xl font-extrabold text-center mb-14 tracking-wide text-gray-900">
          Featured Products
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-6 max-w-full mx-auto px-4">
          {products.map((product) => (
            <div
              key={product.id}
              className="bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-2xl transition cursor-pointer flex flex-col"
            >
              <img
                src={
                  product.image ||
                  'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80'
                }
                alt={product.name}
                className="w-full h-48 object-cover"
                onError={(e) => {
                  (e.currentTarget as HTMLImageElement).src =
                    'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80';
                }}
              />
              <div className="p-4 flex flex-col flex-grow">
                <h3 className="text-lg font-semibold mb-2 truncate">{product.name}</h3>
                <p className="text-indigo-600 font-bold mb-4">₹{product.price}</p>
                <button className="mt-auto bg-indigo-600 hover:bg-indigo-700 text-white py-2 rounded transition font-semibold">
                  View Details
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Trending Products Section */}
      <section className="py-16 bg-gray-50 max-w-full px-6 md:px-20">
        <h2 className="text-3xl font-extrabold text-center mb-10 tracking-wide text-gray-900">
          Trending Now
        </h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 max-w-7xl mx-auto">
          {[
            { name: 'Drone', img: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=150&q=80' },
            { name: 'VR Headset', img: 'https://images.unsplash.com/photo-1585386959984-a41552247496?auto=format&fit=crop&w=150&q=80' },
            { name: 'Smart Light', img: 'https://images.unsplash.com/photo-1526403227281-60df7b1bbf9b?auto=format&fit=crop&w=150&q=80' },
            { name: 'Portable Speaker', img: 'https://images.unsplash.com/photo-1555617117-08d9d0c17da0?auto=format&fit=crop&w=150&q=80' },
          ].map(({ name, img }, idx) => (
            <div
              key={idx}
              className="bg-white shadow-md p-5 text-center rounded-lg hover:shadow-lg transition cursor-pointer"
            >
              <img src={img} alt={name} className="mb-3 mx-auto rounded-md object-cover h-32 w-full" />
              <p className="font-semibold text-gray-900">{name}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Why Choose Us Section */}
      <section className="bg-indigo-700 text-white py-20 px-6 max-w-full">
        <h2 className="text-4xl font-extrabold text-center mb-10 tracking-wide">
          Why Choose Us
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 text-center max-w-7xl mx-auto">
          <div>
            <h4 className="text-xl font-semibold mb-2">Free Shipping</h4>
            <p>On all orders above ₹499.</p>
          </div>
          <div>
            <h4 className="text-xl font-semibold mb-2">Easy Returns</h4>
            <p>7-day hassle-free return policy.</p>
          </div>
          <div>
            <h4 className="text-xl font-semibold mb-2">Secure Checkout</h4>
            <p>100% secure payments & data protection.</p>
          </div>
        </div>
      </section>

      {/* Testimonials Section */}
      <section className="py-20 bg-gray-100 px-6 max-w-full">
        <h2 className="text-3xl font-extrabold text-center mb-12 tracking-wide text-gray-900">
          What Our Customers Say
        </h2>
        <div className="grid md:grid-cols-3 gap-8 max-w-7xl mx-auto">
          {[
            {
              name: 'Anjali Sharma',
              text: 'Excellent product quality and fast delivery. Highly recommend!',
              avatar:
                'https://randomuser.me/api/portraits/women/68.jpg',
            },
            {
              name: 'Rahul Verma',
              text: 'Great customer service and the gadgets are top-notch.',
              avatar:
                'https://randomuser.me/api/portraits/men/45.jpg',
            },
            {
              name: 'Neha Gupta',
              text: 'I love the variety and affordable prices. Will shop again.',
              avatar:
                'https://randomuser.me/api/portraits/women/44.jpg',
            },
          ].map(({ name, text, avatar }, idx) => (
            <div
              key={idx}
              className="bg-white rounded-lg p-6 shadow-md flex flex-col items-center text-center"
            >
              <img
                src={avatar}
                alt={name}
                className="w-20 h-20 rounded-full mb-4 object-cover"
              />
              <p className="italic mb-4">&quot;{text}&quot;</p>
              <h4 className="font-semibold">{name}</h4>
            </div>
          ))}
        </div>
      </section>

      {/* Newsletter Section */}
      <section className="py-16 bg-indigo-600 text-white px-6 max-w-full">
        <h2 className="text-4xl font-extrabold text-center mb-6 tracking-wide">
          Stay Updated
        </h2>
        <p className="text-center max-w-xl mx-auto mb-8">
          Subscribe to our newsletter for the latest deals and products.
        </p>
        <form
          onSubmit={handleNewsletterSubmit}
          className="max-w-xl mx-auto flex flex-col sm:flex-row gap-4"
        >
          <input
            type="email"
            placeholder="Enter your email"
            value={newsletterEmail}
            onChange={(e) => setNewsletterEmail(e.target.value)}
            className="flex-grow px-4 py-3 rounded text-gray-900 font-medium focus:outline-none"
            required
          />
          <button
            type="submit"
            className="bg-white text-indigo-700 font-bold px-6 py-3 rounded hover:bg-indigo-50 transition"
          >
            Subscribe
          </button>
        </form>
        {newsletterMsg && (
          <p className="text-center mt-4 font-semibold">
            {newsletterMsg}
          </p>
        )}
      </section>

      {/* Footer */}
      <footer className="bg-gray-900 text-gray-400 text-center py-6 mt-10">
        <p>© 2025 Your Company. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
