// client/src/App.tsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Import Layout Components
import Header from './components/Header';
import Footer from './components/Footer';

// Import Page Components
import HomePage from './pages/HomePage';
import ProductDetailPage from './pages/ProductDetailPage';
import CartPage from './pages/CartPage'; // Import the new CartPage
import NotFoundPage from './pages/NotFoundPage';

// Import Context Providers
import { CartProvider } from './context/CartContext'; // Import the CartProvider

const App: React.FC = () => {
  return (
    <Router>
      {/* Wrap the entire application with CartProvider to make cart state accessible */}
      <CartProvider>
        <div className="flex flex-col min-h-screen bg-gray-100 font-sans antialiased">
          {/* Header component for navigation and branding */}
          <Header />

          {/* Main content area, grows to fill available space */}
          <main className="flex-grow container mx-auto px-4 py-6">
            {/* Define application routes */}
            <Routes>
              {/* Route for the homepage, displaying a list of products */}
              <Route path="/" element={<HomePage />} />

              {/* Route for individual product detail pages, using a dynamic ID parameter */}
              <Route path="/product/:id" element={<ProductDetailPage />} />

              {/* New route for the shopping cart page */}
              <Route path="/cart" element={<CartPage />} />

              {/* Catch-all route for any undefined paths, leading to a 404 page */}
              <Route path="*" element={<NotFoundPage />} />
            </Routes>
          </main>

          {/* Footer component for copyright, links, etc. */}
          <Footer />
        </div>
      </CartProvider>
    </Router>
  );
};

export default App;
