// client/src/pages/HomePage.tsx
import React from 'react';
import ProductCard from '../components/ProductCard'; // Assuming this exists
import { Product } from '../types/Product'; // Assuming Product type is defined

const HomePage: React.FC = () => {
  // Mock products for demonstration. In a real app, you'd fetch these from an API.
  const mockProducts: Product[] = [
    {
      id: '1',
      name: 'Stylish Headphones',
      description: 'High-fidelity audio with comfortable earcups and a sleek design. Perfect for music lovers.',
      price: 199.99,
      imageUrl: 'https://placehold.co/400x400/ADD8E6/000000?text=Headphones',
      rating: 4.5,
      numReviews: 120,
      category: 'Electronics',
      countInStock: 10,
    },
    {
      id: '2',
      name: 'Ergonomic Office Chair',
      description: 'Designed for ultimate comfort during long work hours. Features adjustable lumbar support.',
      price: 349.99,
      imageUrl: 'https://placehold.co/400x400/90EE90/000000?text=Office+Chair',
      rating: 4.8,
      numReviews: 85,
      category: 'Furniture',
      countInStock: 5,
    },
    {
      id: '3',
      name: 'Portable Bluetooth Speaker',
      description: 'Compact and powerful speaker with rich bass. Perfect for outdoor adventures.',
      price: 79.99,
      imageUrl: 'https://placehold.co/400x400/FFD700/000000?text=Bluetooth+Speaker',
      rating: 4.2,
      numReviews: 210,
      category: 'Electronics',
      countInStock: 0, // Example: out of stock
    },
    {
      id: '4',
      name: 'Smartwatch Pro',
      description: 'Track your fitness and stay connected on the go with this advanced smartwatch.',
      price: 249.99,
      imageUrl: 'https://placehold.co/400x400/FFA07A/000000?text=Smartwatch',
      rating: 4.6,
      numReviews: 150,
      category: 'Wearables',
      countInStock: 15,
    },
  ];

  return (
    <div className="py-8">
      <h1 className="text-4xl font-bold text-center text-gray-900 mb-10">Welcome to Our Store!</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {mockProducts.map((product) => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
