// client/src/pages/HomePage.tsx
import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import api from '../api/api';
import { Product } from '../types/Product';

const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get<Product[]>('/products');
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return (
      <div className="text-center mt-8 p-4 text-xl font-semibold text-gray-700">
        Loading products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center mt-8 p-4 text-xl text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">

      {/* Hero Section */}
      <section className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white rounded-3xl shadow-lg p-10 flex flex-col md:flex-row items-center justify-between mb-16">
        <div className="md:w-1/2 space-y-6">
          <h1 className="text-4xl md:text-5xl font-extrabold">Find Trusted Services Fast</h1>
          <p className="text-lg md:text-xl opacity-90">
            Connect with skilled workers, verified contractors, and quality services in just one click.
          </p>
          <button className="bg-white text-blue-700 font-semibold px-6 py-3 rounded-full hover:bg-gray-100 transition">
            Get Started
          </button>
        </div>
        <div className="md:w-1/2 mt-6 md:mt-0">
          <img
            src="https://via.placeholder.com/500x300.png?text=Hero+Image"
            alt="Hero"
            className="rounded-xl shadow-2xl"
          />
        </div>
      </section>

      {/* About Section */}
      <section className="mb-16 text-center max-w-3xl mx-auto">
        <h2 className="text-3xl font-bold text-gray-800 mb-4">About Us</h2>
        <p className="text-gray-600 text-lg">
          We are dedicated to bridging the gap between skilled workers and job opportunities. Our platform empowers local laborers, helps contractors find talent, and ensures reliable services for every customer.
        </p>
      </section>

      {/* Services Section */}
      <section className="mb-16">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-10">Our Services</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          <div className="bg-white p-6 shadow-md rounded-xl text-center">
            <img src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png" className="h-16 mx-auto mb-4" alt="Worker" />
            <h3 className="font-semibold text-lg text-blue-700 mb-2">Hire Skilled Workers</h3>
            <p className="text-gray-600">Get access to verified plumbers, electricians, masons, and more.</p>
          </div>
          <div className="bg-white p-6 shadow-md rounded-xl text-center">
            <img src="https://cdn-icons-png.flaticon.com/512/3063/3063826.png" className="h-16 mx-auto mb-4" alt="Contractor" />
            <h3 className="font-semibold text-lg text-blue-700 mb-2">Post Contractor Jobs</h3>
            <p className="text-gray-600">Contractors can post bulk work for fast hiring of multiple workers.</p>
          </div>
          <div className="bg-white p-6 shadow-md rounded-xl text-center">
            <img src="https://cdn-icons-png.flaticon.com/512/3595/3595455.png" className="h-16 mx-auto mb-4" alt="Support" />
            <h3 className="font-semibold text-lg text-blue-700 mb-2">Customer Support</h3>
            <p className="text-gray-600">We ensure every user has a smooth and secure experience on our platform.</p>
          </div>
        </div>
      </section>

      {/* Product Section */}
      <section className="mb-16">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-10">Our Latest Jobs / Products</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      </section>

      {/* Call to Action */}
      <section className="bg-indigo-600 text-white rounded-xl py-12 px-6 text-center">
        <h2 className="text-3xl font-bold mb-4">Ready to Join the Workforce Revolution?</h2>
        <p className="text-lg mb-6">Register as a Worker, Contractor, or Customer and get started today.</p>
        <button className="bg-white text-indigo-600 font-semibold px-8 py-3 rounded-full shadow-md hover:bg-gray-100 transition">
          Register Now
        </button>
      </section>
    </div>
  );
};

export default HomePage;
