// client/src/pages/HomePage.tsx
import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import api from '../api/api';
import { Product } from '../types/Product';

const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get<Product[]>('/products');
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return (
      <div className="text-center mt-8 p-4 text-xl font-semibold text-gray-700">
        Loading products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center mt-8 p-4 text-xl text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* --- Hero Section (Inspired by Figma - adjust content and styling) --- */}
      <section className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-lg shadow-xl overflow-hidden mb-12 p-8 md:p-12 lg:p-16 flex flex-col md:flex-row items-center justify-between">
        <div className="text-center md:text-left md:w-1/2 mb-6 md:mb-0">
          <h2 className="text-4xl md:text-5xl lg:text-6xl font-extrabold leading-tight mb-4 animate-fade-in-down">
            Elevate Your Everyday
          </h2>
          <p className="text-lg md:text-xl lg:text-2xl opacity-90 mb-8 animate-fade-in-up">
            Discover cutting-edge technology and premium lifestyle products.
          </p>
          <button className="bg-white text-blue-700 hover:bg-gray-100 font-bold py-3 px-8 rounded-full shadow-lg transition duration-300 ease-in-out transform hover:scale-105 animate-fade-in">
            Explore Collections
          </button>
        </div>
        <div className="md:w-1/2 flex justify-center md:justify-end">
          {/* Placeholder for an image or illustration */}
          <img
            src="https://via.placeholder.com/400x300/e0e7ff/3f51b5?text=New+Arrivals" // Replace with a relevant image path
            alt="Hero Visual"
            className="rounded-lg shadow-2xl max-w-full h-auto animate-fade-in-right"
          />
        </div>
      </section>

      {/* --- Featured Categories Section --- */}
      <section className="py-12 bg-white rounded-lg shadow-md mb-12">
        <h2 className="text-3xl md:text-4xl font-bold text-gray-800 text-center mb-8">
          Shop by Category
        </h2>
        <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-6 px-4">
          {/* Example Category Card - you'd map over an array of categories here */}
          <div className="flex flex-col items-center p-4 bg-gray-50 rounded-lg shadow-sm hover:shadow-md transition duration-300 transform hover:-translate-y-1">
            <img src="https://via.placeholder.com/80/f0f4f8/607d8b?text=Tech" alt="Electronics" className="w-20 h-20 mb-3 rounded-full" />
            <p className="text-lg font-semibold text-gray-700">Electronics</p>
          </div>
          <div className="flex flex-col items-center p-4 bg-gray-50 rounded-lg shadow-sm hover:shadow-md transition duration-300 transform hover:-translate-y-1">
            <img src="https://via.placeholder.com/80/f0f4f8/607d8b?text=Fashion" alt="Fashion" className="w-20 h-20 mb-3 rounded-full" />
            <p className="text-lg font-semibold text-gray-700">Fashion</p>
          </div>
          <div className="flex flex-col items-center p-4 bg-gray-50 rounded-lg shadow-sm hover:shadow-md transition duration-300 transform hover:-translate-y-1">
            <img src="https://via.placeholder.com/80/f0f4f8/607d8b?text=Home" alt="Home Decor" className="w-20 h-20 mb-3 rounded-full" />
            <p className="text-lg font-semibold text-gray-700">Home & Living</p>
          </div>
          <div className="flex flex-col items-center p-4 bg-gray-50 rounded-lg shadow-sm hover:shadow-md transition duration-300 transform hover:-translate-y-1">
            <img src="https://via.placeholder.com/80/f0f4f8/607d8b?text=Sports" alt="Sports" className="w-20 h-20 mb-3 rounded-full" />
            <p className="text-lg font-semibold text-gray-700">Sports</p>
          </div>
          <div className="flex flex-col items-center p-4 bg-gray-50 rounded-lg shadow-sm hover:shadow-md transition duration-300 transform hover:-translate-y-1">
            <img src="https://via.placeholder.com/80/f0f4f8/607d8b?text=Books" alt="Books" className="w-20 h-20 mb-3 rounded-full" />
            <p className="text-lg font-semibold text-gray-700">Books</p>
          </div>
          <div className="flex flex-col items-center p-4 bg-gray-50 rounded-lg shadow-sm hover:shadow-md transition duration-300 transform hover:-translate-y-1">
            <img src="https://via.placeholder.com/80/f0f4f8/607d8b?text=Beauty" alt="Beauty" className="w-20 h-20 mb-3 rounded-full" />
            <p className="text-lg font-semibold text-gray-700">Beauty</p>
          </div>
        </div>
      </section>

      {/* --- Promotional Banners Section --- */}
      <section className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-12 px-4">
        <div className="bg-gradient-to-br from-purple-500 to-pink-500 text-white p-8 rounded-lg shadow-lg flex flex-col justify-center items-center text-center">
          <h3 className="text-3xl font-bold mb-3">Limited Time Offer!</h3>
          <p className="text-lg mb-5 opacity-90">Get 20% off all Headphones. Don't miss out!</p>
          <button className="bg-white text-purple-700 hover:bg-gray-100 font-bold py-2 px-6 rounded-full transition duration-300 transform hover:scale-105">
            Shop Headphones
