import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import api from '../api/api';
import { Product } from '../types/Product';

const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get<Product[]>('/products');
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return (
      <div className="text-center mt-8 p-4 text-xl font-semibold text-gray-700">
        Loading products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center mt-8 p-4 text-xl text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">

      {/* --- Hero Section --- */}
      <section className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white rounded-2xl shadow-xl overflow-hidden mb-16 p-10 md:p-16 flex flex-col md:flex-row items-center justify-between">
        <div className="text-center md:text-left md:w-1/2 mb-10 md:mb-0">
          <h2 className="text-4xl md:text-5xl font-extrabold leading-tight mb-4">
            Elevate Your Everyday
          </h2>
          <p className="text-lg md:text-xl opacity-90 mb-6">
            Discover cutting-edge technology and premium lifestyle products tailored just for you.
          </p>
          <button className="bg-white text-indigo-700 hover:bg-gray-100 font-bold py-3 px-6 rounded-full shadow-md transition duration-300 hover:scale-105">
            Shop Now
          </button>
        </div>
        <div className="md:w-1/2 flex justify-center md:justify-end">
          <img
            src="https://via.placeholder.com/450x300/e0e7ff/3f51b5?text=New+Arrivals"
            alt="Hero Visual"
            className="rounded-xl shadow-2xl max-w-full h-auto"
          />
        </div>
      </section>

      {/* --- Category Section --- */}
      <section className="mb-16">
        <h3 className="text-3xl font-bold text-center text-gray-800 mb-10">Shop by Category</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 text-center">
          {['Smartphones', 'Headphones', 'Accessories', 'Watches'].map((category, idx) => (
            <div
              key={idx}
              className="bg-gray-100 p-6 rounded-xl shadow hover:shadow-md transition"
            >
              <img
                src={`https://via.placeholder.com/100?text=${category}`}
                alt={category}
                className="mx-auto mb-3"
              />
              <p className="text-lg font-semibold">{category}</p>
            </div>
          ))}
        </div>
      </section>

      {/* --- Products Section --- */}
      <section className="mb-16">
        <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-8 text-center">
          Our Latest Innovations
        </h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 md:gap-8">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      </section>

      {/* --- Testimonials Section --- */}
      <section className="bg-indigo-50 p-10 rounded-xl shadow mb-16">
        <h3 className="text-3xl font-bold text-center text-gray-800 mb-10">What Our Customers Say</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center">
          {['Amazing quality!', 'Fast shipping!', 'Customer service is excellent!'].map((msg, idx) => (
            <div key={idx} className="bg-white p-6 rounded-lg shadow-md">
              <p className="text-gray-700 italic mb-4">"{msg}"</p>
              <div className="text-sm text-gray-600">— User {idx + 1}</div>
            </div>
          ))}
        </div>
      </section>

      {/* --- Newsletter Signup Section --- */}
      <section className="bg-gray-100 p-10 rounded-2xl shadow-xl text-center">
        <h3 className="text-3xl font-bold text-gray-800 mb-4">Stay Updated</h3>
        <p className="text-gray-600 mb-6">Subscribe to our newsletter for exclusive deals and product updates.</p>
        <form className="flex flex-col sm:flex-row items-center justify-center gap-4">
          <input
            type="email"
            placeholder="Enter your email"
            className="w-full sm:w-1/2 px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            type="submit"
            className="bg-indigo-600 text-white px-6 py-2 rounded-lg font-semibold hover:bg-indigo-700 transition"
          >
            Subscribe
          </button>
        </form>
      </section>
    </div>
  );
};

export default HomePage;
