import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import api from '../api/api';
import { Product } from '../types/Product';

const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get<Product[]>('/products');
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return (
      <div className="text-center mt-8 p-4 text-xl font-semibold text-gray-700">
        Loading products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center mt-8 p-4 text-xl text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="font-sans text-gray-800">
      {/* --- Hero Section with Background Image --- */}
      <section className="relative bg-cover bg-center h-[90vh] flex items-center justify-center text-white" style={{ backgroundImage: "url('https://via.placeholder.com/1500x800?text=Tech+Banner')" }}>
        <div className="bg-black bg-opacity-60 p-10 rounded-xl text-center max-w-3xl">
          <h1 className="text-5xl md:text-6xl font-bold mb-6">Revolutionize Your Lifestyle</h1>
          <p className="text-xl mb-8">Premium gadgets, smart products, and exclusive deals.</p>
          <button className="bg-white text-black font-bold px-6 py-3 rounded-full hover:bg-gray-200 transition">
            Shop Now
          </button>
        </div>
      </section>

      {/* --- Category Section --- */}
      <section className="py-16 bg-gray-100">
        <h2 className="text-3xl font-bold text-center mb-12">Shop by Category</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-8 px-4 md:px-16">
          {['Phones', 'Wearables', 'Audio', 'Smart Home'].map((cat, idx) => (
            <div key={idx} className="bg-white rounded-xl p-6 text-center shadow hover:shadow-md transition">
              <img src={`https://via.placeholder.com/100?text=${cat}`} alt={cat} className="mx-auto mb-4" />
              <p className="font-semibold text-lg">{cat}</p>
            </div>
          ))}
        </div>
      </section>

      {/* --- Product Section --- */}
      <section className="py-20 px-4 md:px-16 bg-white">
        <h2 className="text-4xl font-bold text-center mb-12">Featured Products</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      </section>

      {/* --- Trending Products --- */}
      <section className="py-16 bg-gray-50">
        <h2 className="text-3xl font-bold text-center mb-10">Trending Now</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 px-6 md:px-20">
          {['Drone', 'VR Headset', 'Smart Light', 'Portable Speaker'].map((item, idx) => (
            <div key={idx} className="bg-white shadow-md p-5 text-center rounded-lg hover:shadow-lg transition">
              <img src={`https://via.placeholder.com/150?text=${item}`} alt={item} className="mb-3 mx-auto" />
              <p className="font-semibold">{item}</p>
            </div>
          ))}
        </div>
      </section>

      {/* --- Features --- */}
      <section className="bg-indigo-700 text-white py-20 px-6">
        <h2 className="text-4xl font-bold text-center mb-10">Why Choose Us</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 text-center">
          <div>
            <h4 className="text-xl font-semibold mb-2">Free Shipping</h4>
            <p>On all orders above ₹499.</p>
          </div>
          <div>
            <h4 className="text-xl font-semibold mb-2">Easy Returns</h4>
            <p>7-day hassle-free return policy.</p>
          </div>
          <div>
            <h4 className="text-xl font-semibold mb-2">Secure Checkout</h4>
            <p>100% secure payments & data protection.</p>
          </div>
        </div>
      </section>

      {/* --- Testimonials --- */}
      <section className="py-20 bg-gray-100 px-6">
        <h2 className="text-3xl font-bold text-center mb-12">What Our Customers Say</h2>
        <div className="grid md:grid-cols-3 gap-8">
          {['Excellent product range!', 'Lightning-fast delivery!', 'Great customer support!'].map((quote, idx) => (
            <div key={idx} className="bg-white p-6 rounded-xl shadow-md text-center">
              <p className="italic text-gray-700 mb-4">"{quote}"</p>
              <p className="text-gray-600">— Customer {idx + 1}</p>
            </div>
          ))}
        </div>
      </section>

      {/* --- Newsletter Signup --- */}
      <section className="bg-indigo-100 py-16 px-6 text-center">
        <h2 className="text-3xl font-bold mb-4">Subscribe to Our Newsletter</h2>
        <p className="text-gray-700 mb-6">Get updates on latest deals and upcoming product launches.</p>
        <form className="flex flex-col sm:flex-row justify-center gap-4 max-w-xl mx-auto">
          <input type="email" placeholder="Enter your email" className="px-4 py-3 rounded-lg border w-full sm:w-2/3" />
          <button type="submit" className="bg-indigo-600 text-white px-6 py-3 rounded-lg hover:bg-indigo-700 transition">
            Subscribe
          </button>
        </form>
      </section>

      {/* --- Contact CTA --- */}
      <section className="bg-gray-900 text-white py-20 text-center px-6">
        <h2 className="text-4xl font-bold mb-6">Need Help?</h2>
        <p className="mb-8">We're here to assist you with product questions, orders, and more.</p>
        <button className="bg-white text-black px-6 py-3 rounded-full font-semibold hover:bg-gray-200 transition">
          Contact Us
        </button>
      </section>

      {/* --- Footer Placeholder --- */}
      <footer className="bg-gray-800 text-gray-300 text-center py-6">
        &copy; {new Date().getFullYear()} YourCompanyName. All rights reserved.
      </footer>
    </div>
  );
};

export default HomePage;
