// client/src/components/Header.tsx
import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext'; // Import useCart to show item count

const Header: React.FC = () => {
  const { cartItems } = useCart();
  const totalItemsInCart = cartItems.reduce((acc, item) => acc + item.quantity, 0);

  return (
    <header className="bg-gray-800 text-white p-4 shadow-md">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-2xl font-bold hover:text-gray-300 transition-colors duration-200">
          My E-Shop
        </Link>
        <nav>
          <ul className="flex space-x-6">
            <li>
              <Link to="/" className="hover:text-gray-300 transition-colors duration-200">
                Products
              </Link>
            </li>
            <li>
              <Link to="/cart" className="hover:text-gray-300 transition-colors duration-200 relative">
                Cart
                {totalItemsInCart > 0 && (
                  <span className="absolute -top-2 -right-2 bg-red-600 text-white text-xs font-bold rounded-full h-5 w-5 flex items-center justify-center">
                    {totalItemsInCart}
                  </span>
                )}
              </Link>
            </li>
            {/* Add more navigation links here */}
            {/* <li><Link to="/login" className="hover:text-gray-300">Login</Link></li> */}
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
