// client/src/pages/HomePage.tsx
import React, { useEffect, useState } from 'react';
import ProductCard from '../components/ProductCard';
import api from '../api/api';
import { Product } from '../types/Product';

const HomePage: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await api.get<Product[]>('/products');
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products. Please try again later.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return (
      <div className="text-center mt-8 p-4 text-xl font-semibold text-gray-700">
        Loading products...
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center mt-8 p-4 text-xl text-red-600 font-semibold">
        {error}
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      {/* --- Hero Section (Inspired by Figma - adjust content and styling) --- */}
      <section className="bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-lg shadow-xl overflow-hidden mb-12 p-8 md:p-12 lg:p-16 flex flex-col md:flex-row items-center justify-between">
        <div className="text-center md:text-left md:w-1/2 mb-6 md:mb-0">
          <h2 className="text-4xl md:text-5xl lg:text-6xl font-extrabold leading-tight mb-4 animate-fade-in-down">
            Elevate Your Everyday
          </h2>
          <p className="text-lg md:text-xl lg:text-2xl opacity-90 mb-8 animate-fade-in-up">
            Discover cutting-edge technology and premium lifestyle products.
          </p>
          <button className="bg-white text-blue-700 hover:bg-gray-100 font-bold py-3 px-8 rounded-full shadow-lg transition duration-300 ease-in-out transform hover:scale-105 animate-fade-in">
            Explore Collections
          </button>
        </div>
        <div className="md:w-1/2 flex justify-center md:justify-end">
          {/* Placeholder for an image or illustration */}
          <img
            src="https://via.placeholder.com/400x300/e0e7ff/3f51b5?text=New+Arrivals" // Replace with a relevant image path
            alt="Hero Visual"
            className="rounded-lg shadow-2xl max-w-full h-auto animate-fade-in-right"
          />
        </div>
      </section>

      {/* --- Products Section --- */}
      <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-8 text-center">
        Our Latest Innovations
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 md:gap-8">
        {products.map((product) => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
