// client/src/pages/ProductDetailPage.tsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import api from '../api/api';
import { Product } from '../types/Product';

const ProductDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [product, setProduct] = useState<Product | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        // Explicitly tell Axios the type of data it expects
        const response = await api.get<Product>(`/products/${id}`); // <--- HERE
        setProduct(response.data);
      } catch (err) {
        setError('Failed to fetch product details.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchProduct();
    }
  }, [id]);

  if (loading) {
    return <div className="text-center mt-8">Loading product details...</div>;
  }

  if (error) {
    return <div className="text-center mt-8 text-red-600">{error}</div>;
  }

  if (!product) {
    return <div className="text-center mt-8">Product not found.</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <div className="flex flex-col md:flex-row gap-8 bg-white p-6 rounded-lg shadow-lg">
        <div className="md:w-1/2 flex justify-center items-center">
          <img
            src={product.imageUrl}
            alt={product.name}
            className="max-w-full h-auto rounded-lg object-contain"
          />
        </div>
        <div className="md:w-1/2">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">{product.name}</h1>
          <p className="text-xl text-gray-700 mb-4">${product.price.toFixed(2)}</p>
          <div className="flex items-center mb-4">
            <span className="text-yellow-500 text-2xl mr-2">⭐</span>
            <span className="text-gray-800 text-lg font-semibold">{product.rating}</span>
            <span className="text-gray-600 ml-2">({product.numReviews} reviews)</span>
          </div>
          <p className="text-gray-700 mb-6 leading-relaxed">{product.description}</p>
          <div className="mb-6">
            <span className="font-semibold text-gray-800">Category: </span>
            <span className="text-gray-600">{product.category}</span>
          </div>
          <div className="mb-6">
            <span className="font-semibold text-gray-800">Availability: </span>
            {product.countInStock > 0 ? (
              <span className="text-green-600">In Stock ({product.countInStock})</span>
            ) : (
              <span className="text-red-600">Out of Stock</span>
            )}
          </div>
          <button
            className="bg-blue-600 text-white px-8 py-3 rounded-md text-lg font-semibold hover:bg-blue-700 transition-colors duration-300"
            disabled={product.countInStock === 0}
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
